startScript:
window.addEventListener('DOMContentLoaded', (event) => {
    const data = {
        title: 'Welcome to TechSolutions',
        header: {
            title: 'Welcome to TechSolutions'
        },
        nav: [
            { text: 'Home', href: '#welcome' },
            { text: 'Services', href: '#services' },
            { text: 'Contact', href: '#contact' }
        ],
        sections: [
            { id: 'welcome', class: 'welcome-section', title: 'Welcome to TechSolutions', content: 'Your partner in innovative IT solutions.' },
            { id: 'services', class: 'services-section', title: 'Our Services', services: [
                { title: 'Web Development', description: 'Building responsive and modern websites.' },
                { title: 'Mobile App Development', description: 'Creating user-friendly mobile applications.' },
                { title: 'Cloud Solutions', description: 'Offering scalable and secure cloud services.' }
            ]}
        ],
        footer: {
            content: '&copy; 2024 TechSolutions. All rights reserved.'
        }
    };

    document.title = data.title;

    const header = document.createElement('header');
    header.innerHTML = `<h1>${data.header.title}</h1>`;
    document.body.appendChild(header);

    const nav = document.createElement('nav');
    data.nav.forEach(link => {
        const a = document.createElement('a');
        a.href = link.href;
        a.textContent = link.text;
        nav.appendChild(a);
    });
    document.body.appendChild(nav);

    data.sections.forEach(section => {
        const sec = document.createElement('section');
        sec.id = section.id;
        sec.className = section.class;
        const h2 = document.createElement('h2');
        h2.textContent = section.title;
        sec.appendChild(h2);
        if (section.content) {
            const p = document.createElement('p');
            p.textContent = section.content;
            sec.appendChild(p);
        }
        if (section.services) {
            const servicesDiv = document.createElement('div');
            servicesDiv.className = 'services';
            section.services.forEach(service => {
                const serviceDiv = document.createElement('div');
                serviceDiv.className = 'service';
                const h3 = document.createElement('h3');
                h3.textContent = service.title;
                const p = document.createElement('p');
                p.textContent = service.description;
                serviceDiv.appendChild(h3);
                serviceDiv.appendChild(p);
                servicesDiv.appendChild(serviceDiv);
            });
            sec.appendChild(servicesDiv);
        }
        document.body.appendChild(sec);
    });

    const footer = document.createElement('footer');
    footer.innerHTML = data.footer.content;
    document.body.appendChild(footer);
});
